import React from 'react'

export default function Button(props) {

  return (
    <button

    style={{...buttonStyle, ...props.style}}
    onClick={props.onClick}

    >
      {props.text}
    </button>
  )
}

const buttonStyle = {
  padding: 10,
  borderRadius: 5,
  fontSize: 'large',
  backgroundColor: 'lightblue',
  color: 'hotpink',
  cursor: 'pointer',
}
